import React, { useContext } from 'react';
import classes from './css/Difficulty.module.scss';
import { GameStateContext } from '../../context/GameStateContext';
import { motion } from 'framer-motion';

const buttonVariants = {
	hidden: {
		opacity: 1,
		x: '-100vw',
	},
	show: {
		x: 1,
		transition: { delay: 0.5 },
	},
};

const Difficulty = () => {
	const { isTimeOut, setDifficulty } = useContext(GameStateContext);

	const handleClick = (e) => {
		setDifficulty(e.target.value);
	};

	return (
		<>
			{isTimeOut && (
				<motion.div className={classes.difficulty}>
					<button
						className={classes.button}
						onClick={handleClick}
						value='easy'
						variants={buttonVariants}
						initial='hidden'
						animate='show'
					>
						easy
					</button>
					<button
						className={classes.button}
						onClick={handleClick}
						value='medium'
					>
						medium
					</button>
					<button className={classes.button} onClick={handleClick} value='hard'>
						hard
					</button>
				</motion.div>
			)}
		</>
	);
};

export default Difficulty;
