import { useContext, useRef, useEffect } from 'react';
import classes from './css/Timer.module.scss';
import Timer from 'react-compound-timer';
import { ScoreContext } from '../../context/ScoreContext';
import { WordsContext } from '../../context/WordsContext';
import { GameStateContext } from '../../context/GameStateContext';
import { motion } from 'framer-motion';

const timerVariants = {
	hidden: {
		y: '-100vh',
	},
	show: {
		y: 1,
		transition: {
			type: 'tween',
			duration: 1.2,
		},
	},
};

const Countdown = () => {
	const { setScore } = useContext(ScoreContext);
	const { setSubstring, setToHighlight } = useContext(WordsContext);
	const { isTimeOut, setIsTimeOut, setShowPopup } =
		useContext(GameStateContext);

	const button = useRef(null);
	const timer = useRef(null);

	useEffect(() => {
		if (button.current) {
			button.current.focus();
		}
	}, [isTimeOut]);

	return (
		<motion.div variants={timerVariants} initial='hidden' animate='show'>
			<Timer
				// initialTime={30000}
				initialTime={5000}
				ref={timer}
				startImmediately={false}
				lastUnit='s'
				direction='backward'
				timeToUpdate={100}
				checkpoints={[
					{
						time: 0,
