export const getRank = (scores, finalScore) => {
	if (finalScore > scores[0].score) {
		return 1;
	} else if (finalScore < scores[scores.length - 1].score) {
		return scores.length;
	} else {
		return scores.findIndex((entry) => finalScore >= entry.score);
	}
};
