import randomWords from 'random-words';

const generateWords = (difficulty) => {
	if (difficulty === 'easy') {
		return [...new Set(randomWords({ exactly: 1000, maxLength: 6 }))];
	} else if (difficulty === 'medium') {
		const wordArray = randomWords({ exactly: 1000, maxLength: 8 });
		return wordArray.filter((word) => word.length >= 6);
	} else if (difficulty === 'hard') {
		const wordArray = randomWords({ exactly: 5000, maxLength: 12 });
		return wordArray.filter((word) => word.length >= 8);
	}
};

export default generateWords;
